  let ul = $("#novelList");
  var mylist = $('#novelList2');
  //Loop all arrays
  for(let x in list){
    let li = document.createElement("li");//Create li Elements
    let m = document.createElement("img");
    m.setAttribute("src","");
    let a = document.createElement("a");//Create Links for each list
    a.setAttribute("href","");//Set href attribute but leave it empty
    /*
     *Now lets put each list to li Elements with each Links
    */
    a.href = list[x].link; //This method put the array links to elements a
    a.innerHTML = list[x].title; //This method put all titles to each list
    m.src = list[x].img;
    li.append(m);
    li.append(a); //Put all a elements to li
    ul.append(li); //Display all li elements to ul
    let li2 = document.createElement("li"); //Create li Elements
    let a2 = document.createElement("a"); //Create Links for each list
    a2.setAttribute("href", "");
    
    a2.href = list[x].link;
    a2.innerHTML = list[x].title;
    li2.append(a2);
    mylist.append(li2);
  }
  
  //Sortint arrays fron A - Z
  //$(".list li").sort().appendTo(".list");
  
var listitems = mylist.children('li').get();
listitems.sort(function(a, b) {
   return $(a).text().toUpperCase().localeCompare($(b).text().toUpperCase());
})
$.each(listitems, function(idx, itm) { mylist.append(itm); });
    
